const btnToggler = document.querySelector(".burger"),
    mobileNav = document.querySelector(".header__mobile");
btnToggler.onclick = function() { btnToggler.classList.contains("burger_active") ? (btnToggler.classList.remove("burger_active"), mobileNav.classList.remove("header__mobile_show")) : (btnToggler.classList.add("burger_active"), mobileNav.classList.add("header__mobile_show")) };
const anchors = document.querySelectorAll('a[href*="#"]');
for (let e = 0; e < anchors.length; e++) anchors[e].onclick = function(t) { t.preventDefault(); const n = anchors[e].getAttribute("href");
    document.querySelector("" + n).scrollIntoView({ behavior: "smooth", block: "start" }) };